/**
 * Given a list of music videos and a string
 * returns a list of music videos whose title, artist, album
 * include any sequence of characters from the given string
 */

import MusicVideo from "./MusicVideo";

// cache validResults to prevent searching through all videos
// if current search input contains lastSeenSearchInput
var lastSeenSearchInput = "";
var validResults = new Array();

export default function KeywordParser(
  videos: MusicVideo[], // contains all of the songs in a user's Spotify playlists
  searchInput: string
) {
  if (searchInput === "") {
    validResults = [];
    lastSeenSearchInput = "";
    return [];
  }

  var res = [];
  var searchDomain = [];

  if (searchInput.indexOf(lastSeenSearchInput) !== -1)
    // a continuation of a previous search
    searchDomain = validResults;
  else searchDomain = videos;

  for (let i = 0; i < searchDomain.length; i++) {
    const video = searchDomain[i];
    if (
      video.title.indexOf(searchInput) !== -1 ||
      video.album.indexOf(searchInput) !== -1 ||
      video.artist.indexOf(searchInput) !== -1
    )
      res.push(video);
  }

  lastSeenSearchInput = searchInput;
  validResults = res;

  return res;
}

var videos = [
  new MusicVideo(
    "futsal-shuffle.img",
    "Futsal Shuffle",
    "Lil Uzi Vert",
    "Eternal Atake",
    ""
  ),
  new MusicVideo(
    "no-photos.img",
    "No Photos",
    "Don Toliver",
    "Heaven or Hell",
    ""
  ),
  new MusicVideo("futsal-shuffle.img", "RENT FREE", "Russ", "RENT FREE", ""),
  new MusicVideo(
    "futsal-shuffle.img",
    "QUARANTINE CLEAN",
    "Turbo, Gunna, Young Thug",
    "QUARANTINE CLEAN",
    ""
  ),
  new MusicVideo(
    "futsal-shuffle.img",
    "Bored In The House",
    "Tyga, Curtis Roach",
    "Bored In The House",
    ""
  ),
  new MusicVideo("futsal-shuffle.img", "@ MEH", "Playboi Carti", "@ MEH", ""),
];

console.log(KeywordParser(videos, "fu"));
console.log(KeywordParser(videos, "fut"));
console.log(KeywordParser(videos, "futs"));
console.log(KeywordParser(videos, "futsa"));
console.log(KeywordParser(videos, "futsal"));
