import * as React from "react";
import { Component } from "react";

import Main from "./components/Main";
import Navbar from "./components/Navbar";
import MusicVideo from "./util/MusicVideo";

const domain = "http://localhost:9000";

type AppState = {
  currentScreen: string;
};

export default class App extends Component<{}, AppState> {
  state: AppState = {
    currentScreen: "Recently Played on Spotify",
  };

  // testApi() {
  //   const url = "http://localhost:9000/testApi";
  //   fetch(url)
  //     .then((res) => res.text())
  //     .then((res) => this.setState({ apiResponse: res }));
  // }

  login() {
    const url = domain + "/getSettings";
    const body = JSON.stringify({
      username: username,
    });
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: body,
    })
      .then((res) => res.json())
      .then((res) => {
        var bgURL =
          res.background === "random"
            ? getRandomBGURL()
            : 'url("' + res.background + '")';

        this.setState({
          bgURL: bgURL,
          username: username,
          firstName: firstName,
          lastName: lastName,
          loggedIn: loggedIn,
          docTitle: docTitle,
          sorting: res.sorting,
          keywords: res.keywords,
          background: bgURL,
        });
      });
  }

  render() {
    var testVideos = [
      new MusicVideo(
        "futsal-shuffle.img",
        "Futsal Shuffle",
        "Lil Uzi Vert",
        "Eternal Atake",
        ""
      ),
      new MusicVideo(
        "no-photos.img",
        "No Photos",
        "Don Toliver",
        "Heaven or Hell",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "RENT FREE",
        "Russ",
        "RENT FREE",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "QUARANTINE CLEAN",
        "Turbo, Gunna, Young Thug",
        "QUARANTINE CLEAN",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "Bored In The House",
        "Tyga, Curtis Roach",
        "Bored In The House",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "@ MEH",
        "Playboi Carti",
        "@ MEH",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "ALASKA",
        "BROCKHAMPTON",
        "SATURATION III",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "Jersey",
        "Future",
        "What A Time To Be Alive",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "All Bad",
        "Future, Lil Uzi Vert",
        "High Off Life",
        ""
      ),
    ];
    return (
      <>
        <button className="btn btn-success" onClick={() => this.login()}>
          Login with Spotify
        </button>
        <Navbar videos={testVideos} />
        <Main title={this.state.currentScreen} videos={testVideos} />
      </>
    );
  }
}
