import * as React from "react";
import { Component } from "react";
import Cookies from "universal-cookie";

import Main from "./components/Main";
import Navbar from "./components/Navbar";
import MusicVideo from "./util/MusicVideo";
import URLParser from "./util/URLParser";

const domain = "http://localhost:9000";

type AppState = {
  currentScreen: string;
  loggedIn: boolean;
  recentlyPlayed: MusicVideo[];
  accessToken: string;
};

export default class App extends Component<{}, AppState> {
  state: AppState = {
    currentScreen: "Recently Played on Spotify",
    loggedIn: false,
    recentlyPlayed: [],
    accessToken: "",
  };

  testApi() {
    const url = "http://localhost:9000/testApi";
    fetch(url)
      .then((res) => res.text())
      .then((res) => console.log(res));
  }

  login() {
    const url = domain + "/mtvApi/login";
    fetch(url)
      .then((authURL) => authURL.text())
      .then((authURL) => (window.location.href = authURL));
  }

  checkLoggedIn() {
    const cookies = new Cookies();
    if (cookies.get("spotifyMTVLoggedIn")) {
      this.setState({ loggedIn: true });
      cookies.remove("spotifyMTVLoggedIn");
    }
  }

  componentDidMount() {
    const cookies = new Cookies();
    console.log("authToken: " + cookies.get("spotifyMTVAccessToken"));

    const url = window.location.href;
    const authCode = URLParser(url, "code=");

    if (authCode !== "") {
      const url = domain + "/mtvApi/callback?code=" + authCode;
      fetch(url)
        .then((authInfo) => authInfo.json())
        .then((authInfo) => {
          cookies.set("spotifyMTVAccessToken", authInfo.accessToken);
          window.location.href = "/";
        });
    }

    if (!this.state.loggedIn) this.checkLoggedIn();
  }

  componentDidUpdate() {
    if (this.state.recentlyPlayed.length === 0 && this.state.loggedIn) {
      // request all of the user's playlists, top artists, and recently played
    }
  }

  logDisplayName() {
    const url = "http://localhost:9000/mtvApi/getRecentlyPlayed";
    fetch(url)
      .then((res) => res.text())
      .then((res) => console.log(res));
  }

  render() {
    var testVideos = [
      new MusicVideo(
        "futsal-shuffle.img",
        "Futsal Shuffle",
        "Lil Uzi Vert",
        "Eternal Atake",
        ""
      ),
      new MusicVideo(
        "no-photos.img",
        "No Photos",
        "Don Toliver",
        "Heaven or Hell",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "RENT FREE",
        "Russ",
        "RENT FREE",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "QUARANTINE CLEAN",
        "Turbo, Gunna, Young Thug",
        "QUARANTINE CLEAN",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "Bored In The House",
        "Tyga, Curtis Roach",
        "Bored In The House",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "@ MEH",
        "Playboi Carti",
        "@ MEH",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "ALASKA",
        "BROCKHAMPTON",
        "SATURATION III",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "Jersey",
        "Future",
        "What A Time To Be Alive",
        ""
      ),
      new MusicVideo(
        "futsal-shuffle.img",
        "All Bad",
        "Future, Lil Uzi Vert",
        "High Off Life",
        ""
      ),
    ];

    if (this.state.loggedIn)
      return (
        <>
          <button
            className="btn btn-sm btn-success m-5"
            onClick={() => this.logDisplayName()}
          >
            Log Display Name
          </button>
          <Navbar videos={testVideos} />
          <Main title={this.state.currentScreen} videos={testVideos} />
        </>
      );
    else
      return (
        <>
          <div className="d-flex justify-content-center">
            <button
              className="btn btn-sm btn-success m-5"
              onClick={() => this.login()}
            >
              Login with Spotify
            </button>
          </div>
        </>
      );
  }
}
