export default class Video {
  thumbnailURI: string;
  title: string;
  artist: string;
  views: number;
  publishDate: string;
  videoURI: string;

  constructor(
    thumbnailURI: string,
    title: string,
    artist: string,
    views: number,
    publishDate: Date,
    videoURI: string
  ) {
    this.thumbnailURI = thumbnailURI;
    this.title = title;
    this.artist = artist;
    this.views = views;
    this.publishDate = this.getWhenPublished(publishDate);
    this.videoURI = videoURI;
  }

  // helper method for debugging
  print() {
    console.log(
      "title:",
      this.title,
      "artist: ",
      this.artist,
      "thumbnail URI: ",
      this.thumbnailURI,
      "views:",
      this.views,
      "date published:",
      this.publishDate,
      "video URI",
      this.videoURI
    );
  }

  // converts the date the video was published into its natural language equivalent
  // TODO: there might be a npm library for this
  getWhenPublished(dateObj: Date) {
    const today = new Date();
    const currYear = today.getFullYear();
    const currMonth = today.getMonth() + 1;
    const currDate = today.getDate();

    const year = dateObj.getFullYear();
    const month = dateObj.getMonth() + 1;
    const date = dateObj.getDate();

    const yearDiff = currYear - year;
    const monthDiff = currMonth - month;
    const dateDiff = currDate - date;

    const sameYear = yearDiff === 0;
    const sameMonth = sameYear && monthDiff === 0;
    const sameDay = sameYear && sameMonth && dateDiff === 0;

    if (sameDay) return "Today";
  }
}
