import React from "react";

import SidebarItem from "./SidebarItem";

import Playlist from "../util/Playlist";
import Artist from "../util/Artist";

import "../stylesheets/Sidebar.css";

type SidebarProps = {
  currentScreen: string;
  // home
  // recently played
  playlists: { [id: string]: Playlist };
  topArtists: { [id: string]: Artist };
  displaying: boolean;
  selectSidebarItem: (title: string, type: string) => void;
};

export default function Sidebar({
  currentScreen,
  playlists,
  displaying,
  selectSidebarItem,
}: SidebarProps) {
  return (
    <div id="sidebar" className={displaying ? "sidebar-displaying" : ""}>
      <SidebarItem
        title="Home"
        id="Home"
        currentScreen={currentScreen}
        type="Home"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="Recently Played"
        currentScreen={currentScreen}
        type="Recently Played"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="ice"
        currentScreen={currentScreen}
        type="Playlist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="yellow"
        currentScreen={currentScreen}
        type="Playlist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="a very long playlist name"
        currentScreen={currentScreen}
        type="Playlist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="Young Thug"
        currentScreen={currentScreen}
        type="Artist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="RL Grime"
        currentScreen={currentScreen}
        type="Artist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="Pop Smoke"
        currentScreen={currentScreen}
        type="Artist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="BROCKHAMPTON"
        currentScreen={currentScreen}
        type="Artist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
      <SidebarItem
        title="Excision"
        currentScreen={currentScreen}
        type="Artist"
        selectSidebarItem={(title: string, type: string) =>
          selectSidebarItem(title, type)
        }
      />
    </div>
  );
}
